/*CAAS应用配置表*/

CREATE TABLE `UC_CAAS_CFG` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `UC_APP_CODE` varchar(10) COLLATE utf8_bin NOT NULL COMMENT '分配用户应用ID',
  `ORG_CODE` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '机构号',
  `CAAS_APP_CODE` varchar(10) COLLATE utf8_bin NOT NULL COMMENT 'CAAS应用ID',
  `APP_DES` varchar(100) COLLATE utf8_bin NOT NULL COMMENT '项目应用描述',
  `CAAS_API_URL` varchar(100) COLLATE utf8_bin NOT NULL COMMENT 'caas url',
  `SSL_ENABLED` tinyint(1) NOT NULL COMMENT '是否https',
  `PROXY_ENABLED` tinyint(1) NOT NULL COMMENT '是否代理',
  `PROXY_HOST` varchar(100) COLLATE utf8_bin NOT NULL COMMENT '代理host',
  `PROXY_PORT` varchar(10) COLLATE utf8_bin NOT NULL COMMENT '代理port',
  `APPKEY` varchar(100) COLLATE utf8_bin NOT NULL COMMENT 'appkey',
  `APPSECRET` varchar(100) COLLATE utf8_bin NOT NULL COMMENT 'appserect',
  `SD` int(2) NOT NULL COMMENT '软件环境1开发2测试3UAT4线上',
  `EXPIRE_TIME` int(10) DEFAULT NULL COMMENT 'token过期时间',
  `CREATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `UPDATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;


/*key资源池表*/

CREATE TABLE `UC_KEY_POOL` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `UC_KEY` varchar(64) COLLATE utf8_bin NOT NULL COMMENT 'key',
  `DES` varchar(200) COLLATE utf8_bin DEFAULT NULL COMMENT '描述',
  `CREATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '记录创建时间',
  `UPDATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '记录更新时间',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
/*步骤配置表*/

CREATE TABLE `UC_STEP_CFG` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `APP_CODE` varchar(10) COLLATE utf8_bin NOT NULL COMMENT '应用CODE',
  `PRO_ID` varchar(10) COLLATE utf8_bin DEFAULT NULL COMMENT '产品Id',
  `STEP_EKY` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '步骤key',
  `SORTED` int(2) DEFAULT NULL COMMENT '排序序号',
  `STATUS` int(2) NOT NULL DEFAULT '1' COMMENT '状态1可用0失效',
  `CREATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '记录创建时间',
  `UPDATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '记录更新时间',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;
/*步骤明细配置表*/

CREATE TABLE `UC_STEP_DETAIL_CFG` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `APP_CODE` varchar(10) COLLATE utf8_bin NOT NULL COMMENT '应用CODE',
  `PRO_ID` varchar(10) COLLATE utf8_bin DEFAULT NULL COMMENT '产品ID',
  `STEP_KEY` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '步骤key',
  `STEP_KEY_DETAIL` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '步骤明细key',
  `IS_EMPTY` int(2) NOT NULL COMMENT '是否为空1可空2不可空',
  `TYPE` int(2) DEFAULT '1' COMMENT '1-String,2-数字，3-时间',
  `MATCH_RULE` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '明细key校验规则',
  `STATUS` int(2) DEFAULT '1' COMMENT '状态1可用0失效',
  `CREATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '记录创建时间',
  `UPDATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '记录更新时间',
   PRIMARY KEY (`ID`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

/*步骤状态业务表*/

CREATE TABLE `UC_BUSYNESS_MAIN` (
  `ID` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '自增主键',
  `USER_ID` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '用户ID',
  `ORG_CODE` varchar(32) COLLATE utf8_bin NOT NULL COMMENT '机构号',
  `APP_CODE` varchar(10) COLLATE utf8_bin NOT NULL COMMENT '应用code',
  `PRO_ID` varchar(10) COLLATE utf8_bin DEFAULT NULL COMMENT '产品ID',
  `STEP_KEY` varchar(64) COLLATE utf8_bin NOT NULL COMMENT '步骤KEY',
  `STEP_STATE` varchar(20) COLLATE utf8_bin NOT NULL COMMENT '步骤状态1已提交2未提交',
  `CREATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '记录创建时间',
  `UPDATED_TIME` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '记录更新时间',
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin;

